// SPDX-License-Identifier: UNLICENSED
pragma solidity >=0.6.0 <0.8.0;

import "@openzeppelin/contracts/access/Ownable.sol";
import "@openzeppelin/contracts/math/SafeMath.sol";
import "@openzeppelin/contracts/token/ERC20/IERC20.sol";

import "@openzeppelin/contracts/token/ERC20/SafeERC20.sol";

interface IWPI {
    function deposit() external payable;
    function transfer(address to, uint256 value) external returns (bool);
    function withdraw(uint256) external;
    function balanceOf(address usr) external view returns (uint256);
}

contract PiStakingEpoch is Ownable {
    using SafeMath for uint256;
    using SafeERC20 for IERC20;

    // Info of each user.
    struct UserInfo {
        uint256 amount;     // How many PI tokens the user has staked.
        uint256 rewardDebt; // Reward debt. 
        bool inBlackList;
        uint256 userEpoch;
    }

    // Info of each pool.
    struct PoolInfo {
        uint256 lastRewardBlock;  // Last block number that PNFT distribution occurs.
        uint256 accCakePerShare; // Accumulated PNFT per share, times 1e12. See below.

        uint256 totalAmount; // The total amount of current epoch
        uint256 startBlock; // The block number when Token mining starts.
        uint256 endBlock; // The block number when Token mining ends.
    }

    // The REWARD TOKEN: PNFT
    IERC20 public rewardToken;

    // adminAddress
    address public adminAddress;

    // WPI contract address
    address public WPI;

    // PNFT tokens created per block.
    uint256 public rewardPerBlock;

    // Info of each user that stakes PI tokens.
    mapping (address => UserInfo) public userInfo;

    //current epoch
    uint256 internal currentEpoch;
    //Info of each epoch that use pool info
    mapping(uint256 => PoolInfo) public epochPool;

    // limit 10000000 PI here
    uint256 public limitAmount = 10000000000000000000000000;

    event Deposit(address indexed user, uint256 indexed epoch, uint256 amount);
    event Withdraw(address indexed user, uint256 indexed epoch, uint256 amount);
    event EmergencyWithdraw(address indexed user, uint256 amount);

    constructor(
        IERC20 _rewardToken,
        uint256 _rewardPerBlock,
        uint256 _startBlock,
        uint256 _bonusEndBlock,
        address _adminAddress,
        address _wpi
    ) public {
        rewardToken = _rewardToken;
        rewardPerBlock = _rewardPerBlock;

        adminAddress = _adminAddress;
        WPI = _wpi;

        currentEpoch = 0;

        // staking pool
        epochPool[currentEpoch] = PoolInfo({
            lastRewardBlock: _startBlock,
            accCakePerShare: 0,
            totalAmount: 0,
            startBlock: _startBlock,
            endBlock: _bonusEndBlock
        });
    }

    modifier onlyAdmin() {
        require(msg.sender == adminAddress, "admin: wut?");
        _;
    }

    receive() external payable {
        assert(msg.sender == WPI); // only accept PI via fallback from the WPI contract
    }

    // Update admin address by the previous dev.
    function setAdmin(address _adminAddress) public onlyOwner {
        adminAddress = _adminAddress;
    }

    function setBlackList(address _blacklistAddress) public onlyAdmin {
        userInfo[_blacklistAddress].inBlackList = true;
    }

    function removeBlackList(address _blacklistAddress) public onlyAdmin {
        userInfo[_blacklistAddress].inBlackList = false;
    }

    function setRewardPerBlock(uint256 _rewardCount) public onlyOwner {
        rewardPerBlock = _rewardCount;
    }

    // Set the limit amount. Can only be called by the owner.
    function setLimitAmount(uint256 _amount) public onlyOwner {
        limitAmount = _amount;
    }

    //add pool info for next epoch
    function addEpochPool(uint256 _startBlock, uint256 _bonusEndBlock) public onlyOwner {
        currentEpoch = currentEpoch.add(1);

        // staking pool
        epochPool[currentEpoch] = PoolInfo({
            lastRewardBlock: _startBlock,
            accCakePerShare: 0,
            totalAmount: 0,
            startBlock: _startBlock,
            endBlock: _bonusEndBlock
        });
    }

    //update pool info for specific epoch
    function updateEpochPool(uint256  _epoch, uint256 _startBlock, uint256 _bonusEndBlock ) public onlyOwner {
        require(_epoch <= currentEpoch, "PiStakingWithEpoch: wrong epoch");

        PoolInfo storage pool = epochPool[_epoch];
        pool.startBlock = _startBlock;
        pool.endBlock = _bonusEndBlock;
        pool.lastRewardBlock = _startBlock;
    }

    // Return reward multiplier over the given _from to _to block.
    function getMultiplier(uint256 _from, uint256 _to,uint256 _epoch) public view returns (uint256) {
        PoolInfo storage pool = epochPool[_epoch];

        if (_to <= pool.endBlock) {
            return _to.sub(_from);
        } else if (_from >= pool.endBlock) {
            return 0;
        } else {
            return pool.endBlock.sub(_from);
        }
    }

    // View function to see pending Reward on frontend.the real reward you get is not this value
    function pendingReward(address _user) external view returns (uint256) {
        UserInfo storage user = userInfo[_user];
        uint256 _epoch = user.userEpoch;
        require(_epoch <= currentEpoch, "PiStakingEpoch: wrong epoch");

        PoolInfo storage pool = epochPool[_epoch];

        uint256 accCakePerShare = pool.accCakePerShare;
        uint256 lpSupply = pool.totalAmount;

        if (block.number > pool.lastRewardBlock && lpSupply != 0) {
            uint256 multiplier = getMultiplier(pool.lastRewardBlock, block.number, _epoch);
            uint256 tokenReward = multiplier.mul(rewardPerBlock);

            accCakePerShare = accCakePerShare.add(tokenReward.mul(1e12).div(lpSupply));
        }
        return user.amount.mul(accCakePerShare).div(1e12).sub(user.rewardDebt);
    }

    // Update reward variables of the given pool to be up-to-date.
    function updatePool(uint256 _epoch) public {
        PoolInfo storage pool = epochPool[_epoch];

        if (block.number <= pool.lastRewardBlock) {
            return;
        }
        uint256 lpSupply = pool.totalAmount;
        if (lpSupply == 0) {
            pool.lastRewardBlock = block.number;
            return;
        }
        uint256 multiplier = getMultiplier(pool.lastRewardBlock, block.number, _epoch);
        uint256 tokenReward = multiplier.mul(rewardPerBlock);

        pool.accCakePerShare = pool.accCakePerShare.add(tokenReward.mul(1e12).div(lpSupply));
        pool.lastRewardBlock = block.number;
    }

    function setMark() external {
        UserInfo storage user = userInfo[msg.sender];

        uint256 oldEpoch = user.userEpoch;
        PoolInfo storage oldPool = epochPool[oldEpoch];

        updatePool(oldEpoch);
        if (user.amount > 0) {
             uint256 pending = user.amount.mul(oldPool.accCakePerShare).div(1e12).sub(user.rewardDebt);
            if(pending > 0) {
                rewardToken.safeTransfer(address(msg.sender), pending);
            }
        }
        oldPool.totalAmount = oldPool.totalAmount.sub(user.amount);
        user.userEpoch = currentEpoch;
        user.rewardDebt = 0;

        PoolInfo storage currentPool = epochPool[currentEpoch];

        currentPool.totalAmount = currentPool.totalAmount.add(user.amount); 
        updatePool(currentEpoch);
    }

    // Stake tokens to SmartChef
    function deposit() public payable {
        PoolInfo storage pool = epochPool[currentEpoch];

        UserInfo storage user = userInfo[msg.sender];

        if (user.amount == 0) {
            user.userEpoch = currentEpoch;
        }

        require(user.userEpoch == currentEpoch, "PiStakingWithEpoch: user epoch is wrong");

        require (user.amount.add(msg.value) <= limitAmount, 'exceed the top');
        require (!user.inBlackList, 'in black list');

        updatePool(currentEpoch);
        if (user.amount > 0) {
            uint256 pending = user.amount.mul(pool.accCakePerShare).div(1e12).sub(user.rewardDebt);
            if(pending > 0) {
                rewardToken.safeTransfer(address(msg.sender), pending);
            }
        }
        if(msg.value > 0) {
            IWPI(WPI).deposit{value: msg.value}();
            assert(IWPI(WPI).transfer(address(this), msg.value));
            user.amount = user.amount.add(msg.value);

            pool.totalAmount =  pool.totalAmount.add(msg.value);
        }
        user.rewardDebt = user.amount.mul(pool.accCakePerShare).div(1e12);

        emit Deposit(msg.sender,currentEpoch, msg.value);
    }

    function safeTransferPI(address to, uint256 value) internal {
        (bool success, ) = to.call{gas: 23000, value: value}("");

        require(success, 'TransferHelper: PI_TRANSFER_FAILED');
    }

    // Withdraw tokens from STAKING.
    function withdraw(uint256 _amount) public {
        UserInfo storage user = userInfo[msg.sender];
        require(user.amount >= _amount, "withdraw: not good");

        uint256 userEpoch = user.userEpoch;

        PoolInfo storage pool = epochPool[userEpoch];

        require(pool.totalAmount >= _amount, "withdraw: not good pool amount");

        updatePool(userEpoch);
        uint256 pending = user.amount.mul(pool.accCakePerShare).div(1e12).sub(user.rewardDebt);
        if(pending > 0 && !user.inBlackList) {
            rewardToken.safeTransfer(address(msg.sender), pending);
        }

        if(_amount > 0) {
            user.amount = user.amount.sub(_amount);
            IWPI(WPI).withdraw(_amount);
            safeTransferPI(address(msg.sender), _amount);

            pool.totalAmount = pool.totalAmount.sub(_amount);
        }
        user.rewardDebt = user.amount.mul(pool.accCakePerShare).div(1e12);

        emit Withdraw(msg.sender,userEpoch, _amount);
    }

    // Withdraw without caring about rewards. EMERGENCY ONLY.
    function emergencyWithdraw() public {
        UserInfo storage user = userInfo[msg.sender];

        if (user.amount > 0) {
            IWPI(WPI).withdraw(user.amount);
            safeTransferPI(address(msg.sender), user.amount);
        }

        emit EmergencyWithdraw(msg.sender, user.amount);
        user.amount = 0;
        user.rewardDebt = 0;
    }

    // Withdraw reward. EMERGENCY ONLY.
    function emergencyRewardWithdraw(uint256 _amount) public onlyOwner {
        require(_amount < rewardToken.balanceOf(address(this)), 'not enough token');
        rewardToken.safeTransfer(address(msg.sender), _amount);
    }

    function getStaking(address _user) public view returns (uint256) {
        UserInfo storage user = userInfo[_user];
        return user.amount;
    }

    function getUserEpoch(address _user) public view returns (uint256) {
        UserInfo storage user = userInfo[_user];
        return user.userEpoch;
    }

    function getCurrentEpoch() public view returns (uint256) {
        return currentEpoch;
    }

    function getEpochBlockNumber(uint256 _epoch) public view returns (uint256, uint256) {
        PoolInfo storage pool = epochPool[_epoch];

        return (pool.startBlock, pool.endBlock);
    }

    function getTotalSupply() public view returns (uint256) {
        return IWPI(WPI).balanceOf(address(this));
    }

    function getPoolSupply(uint256 _epoch) public view returns (uint256) {
        PoolInfo storage pool = epochPool[_epoch];

        return pool.totalAmount;
    }
}
